static std::mutex mapmutex;

int UdpSendMap::OpenUdp(const char *remotehostname, const char *remoteportno) {
	if ((UdpLocalPort=UdpMapSender.OpenUdpPort(0))==NULL)  // Open UDP Network port
	{
		printf("OpenUdpPort Failed\n");
		CloseUdp();
		return 0;
	}

	if  ((UdpDest=UdpMapSender.GetUdpDest(remotehostname,remoteportno))==NULL)  // Setup remote network destination to send images
	{
		printf("GetUdpDest Failed\n");
		CloseUdp();
		return 0;
	}

	int size = sizeof(unsigned char) + (sizeof(unsigned char)*MAP_SIZE); // 1 byte header + (20 x 20 map and 8byte map information)
	Map = (unsigned char *)malloc(size);
	memset(Map, 0x00, size);

	unsigned char header = 0x4;
	std::memcpy(Map, &header, sizeof(unsigned char));

	// TODO: Eliminate after testing
	// For testing
	std::memcpy(Map+sizeof(unsigned char), &map1, MAP_SIZE);

	// For debugging
	printf("Map size: %d\n", size);
	printf("int size: %d\n", sizeof(int));
	printf("double size: %d\n", sizeof(double));

	// Start thread for sending map to Remote UI
	CreateThreadToSendMap();

	return 1;
}

//-----------------------------------------------------------------
// UdpSendImageAsJpeg - Sends a Open CV Mat Image commressed as a 
// jpeg image in side a UDP Message on the specified UDP local port
// and Destination. return bytes sent on success and -1 on failure
//-----------------------------------------------------------------
void UdpSendMap::SetMap(unsigned char *map)
{
	mapmutex.lock();
	std::memcpy(Map+sizeof(unsigned char), &map1, MAP_SIZE);
	mapmutex.unlock();

	return;
}

void UdpSendMap::CloseUdp() {
	UdpMapSender.CloseUdpPort(&UdpLocalPort);
	UdpMapSender.DeleteUdpDest(&UdpDest);
	if (Map != NULL) {
		free((void *)Map);
		Map = NULL;
	}
	return;
}

void UdpSendMap::SendMap()
{
	// For debugging
	printf("Send map thread is running!!\n");

	// For testing
	int count = 1;

	while (1)
	{
		if (Map == NULL) {
			printf("Map is NULL!\n");
			continue;
		}

		mapmutex.lock();
		if((UdpMapSender.SendUDPMsg(UdpLocalPort, UdpDest, Map+sizeof(unsigned char), MAP_SIZE)) == -1) {
			printf("Error to send map to Remote UI");
		}
		mapmutex.unlock();

		// For debugging
		printf("Send map!\n");

		sleep(1);

		// For testing
		count+=1;

		switch (count) {
		case 3:
			std::memcpy(Map+sizeof(unsigned char), &map2, MAP_SIZE);
			break;
		case 6:
			std::memcpy(Map+sizeof(unsigned char), &map3, MAP_SIZE);
			break;
		case 9:
			std::memcpy(Map+sizeof(unsigned char), &map4, MAP_SIZE);
			break;
		case 12:
			std::memcpy(Map+sizeof(unsigned char), &map5, MAP_SIZE);
			count =0;
			break;
		}
	}
}

void UdpSendMap::CreateThreadToSendMap(void)
{
	// Send map thread
	MapThread = new std::thread(&UdpSendMap::SendMap, this);
}



